window.HN&&window.jQuery&&(HN.tab=function($tabId){HN.debug('HN.tab is init');var options={tabId:'tab-id',index:0,eventType:'click',tabClass:'selecteds',autoSwitch:false,autoSwitchTime:5000,ajax:null};if(HN.isString($tabId)){options.tabId=$tabId;}else if(typeof $tabId=='object'){$.extend(options,$tabId);}var autoStart,t=$('#'+options.tabId),bar=t.find('div:first > ul > li'),content=t.find('.content div'),n=options.index;content.hide();if(HN.isString(options.tabClass)){bar.eq(options.index).addClass(options.tabClass);}else if(typeof options.tabClass=='object'){bar.eq(options.index).addClass(options.tabClass[options.index]);}if(options.ajax&&!content.eq(options.index).hasClass('itgeted')){ajaxGet(options.index);}content.eq(options.index).show(0);if(options.autoSwitch){autoStart=setInterval(auto,options.autoSwitchTime,n);}bar.each(function(i){$(this).bind(options.eventType,function(){if(options.autoSwitch){clearInterval(autoStart);n=i;autoStart=setInterval(auto,options.autoSwitchTime,n);}setTab(i);setContent(i);});});function setTab($i){if(HN.isString(options.tabClass)){bar.removeClass(options.tabClass);bar.eq($i).addClass(options.tabClass);}else if(typeof options.tabClass=='object'){bar.each(function(j){bar.eq(j).removeClass(options.tabClass[j]);});bar.eq($i).addClass(options.tabClass[$i]);}}function setContent($i){content.hide();if(options.ajax&&!content.eq($i).hasClass('itgeted')){ajaxGet($i);}content.eq($i).fadeIn(1000);}function ajaxGet($i){$.ajax({type:options.ajax.type,url:options.ajax.url,data:"num="+$i,success:function(msg){content.eq($i).html(msg).addClass('itgeted');}});}function auto(){n++;if(n>bar.length-1){n=0;}setTab(n);setContent(n);}})